mapper.select:
select ut.*,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.create_dt AS urt_create_dt,
rt.* from user_tb ut join user_role_tb urt on (urt.user_id = ut.user_id) join role_tb rt on (rt.role_id = urt.role_id) where ut.user_id = #{userId};

select ut.*,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.create_dt AS urt_create_dt,
rt.* from user_tb ut join user_role_tb urt on (urt.user_id = ut.user_id) join role_tb rt on (rt.role_id = urt.role_id) where ut.email = #{email};

select ut.*,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.create_dt AS urt_create_dt,
rt.* from user_tb ut join user_role_tb urt on (urt.user_id = ut.user_id) join role_tb rt on (rt.role_id = urt.role_id) where ut.username = #{username};

select ut.*,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id, urt.create_dt AS urt_create_dt, urt.create_dt AS urt_create_dt,
rt.* from user_tb ut join user_role_tb urt on (urt.user_id = ut.user_id) join role_tb rt on (rt.role_id = urt.role_id) where urt.role_id = 2 or urt.role_id = 3;
